elements.expression:
filters
| essql query="SELECT service_name, COUNT(*) as count FROM logs GROUP BY service_name ORDER BY count DESC LIMIT 10"
| pointseries x="service_name" y="count" color="service_name"
| plot defaultStyle={seriesStyle bars=0.8 horizontalBars=false}
  legend=false
  font={font family="Arial" size=14 color="#ffffff"}
  palette={palette "#00d9ff" "#5f27cd" "#ff6348" "#ffa502" "#1e90ff" "#2ed573" "#ff4757" "#3742fa" "#ff6348" "#ffd32a"}
| render css=".chart { background: #16213e; border-radius: 8px; padding: 10px; }"

filters
| essql query="SELECT DATE_TRUNC('minute', @timestamp) as time, service_name, COUNT(*) as count FROM logs GROUP BY time, service_name ORDER BY time"
| pointseries x="time" y="count" color="service_name"
| plot defaultStyle={seriesStyle lines=3 fill=false}
  legend="ne"
  yaxis=true
  xaxis=true
  font={font family="Arial" size=14 color="#ffffff"}
  palette={palette "#00d9ff" "#5f27cd" "#ff6348" "#ffa502" "#1e90ff" "#2ed573" "#ff4757" "#3742fa"}
| render css=".chart { background: #16213e; border-radius: 8px; padding: 10px; }"
